import{_ as i,o as e,c as a,e as l}from"./app-66d6b4fd.js";const r="/blog/seckill/Snipaste_2023-06-26_16-00-24.png",t={},n=l('<h1 id="seckill秒杀系统" tabindex="-1"><a class="header-anchor" href="#seckill秒杀系统" aria-hidden="true">#</a> Seckill秒杀系统</h1><h2 id="为什么要搞秒杀系统" tabindex="-1"><a class="header-anchor" href="#为什么要搞秒杀系统" aria-hidden="true">#</a> 为什么要搞秒杀系统</h2><ul><li><strong>高并发、大流量场景</strong>下最具代表性的系统。</li><li>秒杀系统背后涉及到的<strong>高并发、高性能、高可用、可扩展的技术思路和架构模式与架构思想</strong>，可以直接复用于任何需要支撑高并发、大流量的业务场景。</li></ul><h2 id="秒杀系统核心技术" tabindex="-1"><a class="header-anchor" href="#秒杀系统核心技术" aria-hidden="true">#</a> 秒杀系统核心技术</h2><p>单就秒杀系统本身而言，就是为应对<strong>瞬时高并发、大流量场景</strong>而设计的支持<strong>高并发、大流量</strong>的系统，其背后会涉及到众多<strong>高并发、高性能、高可用</strong>的技术作为基础保障。同时，在系统中，也要重点突破<strong>库存与限购、防刷与风控、数据一致、热点隔离、动静分离、削峰填谷、数据兜底、限流与降级、流控与容灾</strong>等核心技术问题。</p><h2 id="方案目标" tabindex="-1"><a class="header-anchor" href="#方案目标" aria-hidden="true">#</a> 方案目标</h2><ul><li>业务目标：满足用户故事中的各类场景。</li><li>技术目标：秒杀商品详情页接口：10W+QPS，秒杀接口：1W+TPS。</li><li>架构目标：高并发、高性能、高可用、可监控、可预警、可伸缩。</li></ul><h2 id="技术选型" tabindex="-1"><a class="header-anchor" href="#技术选型" aria-hidden="true">#</a> 技术选型</h2><ul><li>开发框架：SpringBoot。</li><li>缓存：Redis+本地缓存。</li><li>数据库：MySQL。</li><li>业务网关：SpringCloud Gateway。</li><li>持久层框架：MyBatis。</li><li>服务配置与注册发现：Nacos。</li><li>容器：Docker。</li><li>容器化管理：Swarm、Portainer。</li><li>监控：Prometheus、Grafana。</li><li>系统限流： Nginx+Lua、Sentinel。</li><li>消息中间件： RocketMQ。</li><li>单元测试：Junit。</li><li>压测工具：JMeter。</li></ul><h2 id="秒杀系统总体架构设计" tabindex="-1"><a class="header-anchor" href="#秒杀系统总体架构设计" aria-hidden="true">#</a> 秒杀系统总体架构设计</h2><p><img src="'+r+'" alt="Snipaste_2023-06-26_16-00-24"></p>',11),s=[n];function o(h,d){return e(),a("div",null,s)}const _=i(t,[["render",o],["__file","index.html.vue"]]);export{_ as default};
